import React from 'react';

const About = () => {
  return (
    <section id="about" className="py-20 px-10 bg-white">
      <h2 className="text-4xl font-bold mb-6 text-center">About Me</h2>
      <p className="max-w-3xl mx-auto text-center text-lg">
        I'm a passionate Front-End Developer with experience in React, Tailwind CSS, and JavaScript. 
        I enjoy turning ideas into reality through beautiful and functional web applications.
      </p>
    </section>
  );
};

export default About;
